document.addEventListener("DOMContentLoaded",()=>{const projectContainer=document.getElementById("all-projects-list");projectsData.forEach((project,index)=>{const card=document.createElement("div");card.className="project-card fade-in";card.dataset.index=index;card.innerHTML=`\n      <img src="${project.images[0]}" alt="${project.title}" />\n      <div class="project-card-content">\n        <h3>${project.title}</h3>\n        <p>${project.description}</p>\n      </div>\n    `;projectContainer.appendChild(card)});const modal=document.getElementById("project-modal");const modalImages=document.getElementById("modal-images");const modalDescription=document.getElementById("modal-description");const closeBtn=document.querySelector(".close-btn");document.querySelectorAll(".project-card").forEach(card=>{card.addEventListener("click",()=>{const index=card.dataset.index;const project=projectsData[index];modalImages.innerHTML=project.images.map(src=>`<img src="${src}" alt="">`).join("");modalDescription.textContent=project.description;modal.style.display="block"})});closeBtn.addEventListener("click",()=>{modal.style.display="none"});window.addEventListener("click",e=>{if(e.target===modal){modal.style.display="none"}})});