document.addEventListener("DOMContentLoaded",()=>{const homeProjectsContainer=document.getElementById("home-projects-gallery");const shuffledProjects=[...projectsData].sort(()=>.5-Math.random());const selectedProjects=shuffledProjects.slice(0,3);selectedProjects.forEach(project=>{const card=document.createElement("div");card.className="project-item fade-in";card.innerHTML=`\n      <img src="${project.images[0]}" alt="${project.title}">\n      <div class="item-content">\n        <h3>${project.title}</h3>\n        <p>${project.description}</p>\n      </div>\n    `;homeProjectsContainer.appendChild(card)});setTimeout(()=>{document.querySelectorAll(".fade-in").forEach(el=>{el.classList.add("show")})},100);const modal=document.getElementById("project-modal");const modalImages=document.getElementById("modal-images");const modalDescription=document.getElementById("modal-description");const closeBtn=document.querySelector(".close-btn");document.querySelectorAll(".project-item").forEach(card=>{card.addEventListener("click",()=>{const title=card.querySelector("h3").textContent;const project=projectsData.find(p=>p.title===title);if(project){modalImages.innerHTML=project.images.map(img=>`<img src="${img}" alt="">`).join("");modalDescription.textContent=project.description;modal.style.display="block"}})});closeBtn.addEventListener("click",()=>{modal.style.display="none"});window.addEventListener("click",e=>{if(e.target===modal){modal.style.display="none"}})});