const contactForm=document.getElementById("contactForm");if(contactForm){contactForm.addEventListener("submit",function(e){e.preventDefault();document.querySelector(".confirmation-message").style.display="block";setTimeout(()=>{document.querySelector(".confirmation-message").style.display="none"},3e3);this.reset()})}const menuToggle=document.getElementById("menu-toggle");const navbar=document.getElementById("navbar");const openIcon=menuToggle.querySelector(".open-icon");const closeIcon=menuToggle.querySelector(".close-icon");menuToggle.addEventListener("click",function(){navbar.classList.toggle("active");menuToggle.classList.toggle("active")});window.addEventListener("scroll",function(){const header=document.querySelector("header");if(window.scrollY>10){header.classList.add("scrolled")}else{header.classList.remove("scrolled")}});const faders=document.querySelectorAll(".fade-in");const appearOptions={threshold:.2};const appearOnScroll=new IntersectionObserver(function(entries,observer){entries.forEach(entry=>{if(!entry.isIntersecting)return;entry.target.classList.add("show");observer.unobserve(entry.target)})},appearOptions);faders.forEach(fader=>{appearOnScroll.observe(fader)});document.querySelectorAll("#navbar a").forEach(link=>{link.addEventListener("click",()=>{document.getElementById("navbar").classList.remove("active");document.getElementById("menu-toggle").classList.remove("active")})});const modal=document.getElementById("project-modal");const modalImages=document.getElementById("modal-images");const modalDescription=document.getElementById("modal-description");const closeBtn=document.querySelector(".close-btn");const projectDetails={floor1:{images:["https://picsum.photos/seed/floor1a/600/400","https://picsum.photos/seed/floor1b/600/400","https://picsum.photos/seed/floor1c/600/400"],description:"Waterproof vinyl floor installed over leveled base."},kitchen1:{images:["https://picsum.photos/seed/kitchen1a/600/400","https://picsum.photos/seed/kitchen1b/600/400","https://picsum.photos/seed/kitchen1c/600/400"],description:"Modern white cabinets with granite countertops and LED lighting."},electric1:{images:["https://picsum.photos/seed/electric1a/600/400","https://picsum.photos/seed/electric1b/600/400","https://picsum.photos/seed/electric1c/600/400"],description:"Full panel upgrade to 200A with certified wiring."}};document.querySelectorAll(".project-item").forEach(card=>{card.addEventListener("click",()=>{const key=card.querySelector("img").alt.toLowerCase().replace(/\s+/g,"");const data=projectDetails[key];if(data){modalImages.innerHTML=data.images.map(src=>`<img src="${src}" alt="">`).join("");modalDescription.textContent=data.description;modal.style.display="block"}})});closeBtn.addEventListener("click",()=>{modal.style.display="none"});window.addEventListener("click",e=>{if(e.target==modal){modal.style.display="none"}});